    -- Check if total_price is changed
    IF OLD.total_price != NEW.total_price THEN
        SET preValue = CONCAT(preValue, 'total_price = ', OLD.total_price, ', ');
        SET newValueText = CONCAT(newValueText, 'total_price = ', NEW.total_price, ', ');
    ELSE
        SET preValue = CONCAT(preValue, 'total_price = ', OLD.total_price, ', ');
